import React, {useState} from 'react'
import styled from 'styled-components'
import Navbar from '../components/navbar/Navbar'
import Sidebar from '../components/sidebar/Sidebar'
import Pen from '../components/icons/Pen'
import { Facebook, Github, Instagram } from '../components/icons/SocialMedia'

const HomeOuter = styled.div`
  margin: 0;
  padding: 0;
  width: 100vw;
  height: calc(100vh - 80px);
  background: black;
`

const HomeInner = styled.div`
  max-width: 1100px;
  margin: 0 auto;
  padding: 0 20px;
  height: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 10rem;
  position: relative;
`

const HomeLeft = styled.div`
  width: 50%;
  height: 100%;
  padding: 50px;
`

const HomeRight = styled.div`
  padding: 40px;
  color: white;
  width: 50%;
  height: 100%;
  font-size: 1rem;
  text-align: center;
`

const Welcome = styled.div`
  margin: 50px;
  font-size: 3rem;
  font-family: 'Dancing Script', cursive;
`

const Introduction = styled.div`
  font-size: 1.5rem;
  margin-top: 100px;
  margin-bottom: 20px;
`

const Description = styled.div`
  margin-top: 20px;
  margin-bottom: 20px;
  font-size: 2rem;
  font-family: 'Dancing Script', cursive;
`

const SocialLinks = styled.ul`
  display: flex;
  justify-content: center;
`

const Home = () => {
  const [isOpen, setIsOpen] = useState(false)
  const toggle = () => {
    setIsOpen(!isOpen)
  }

  return (
    <>
      <Sidebar isOpen={isOpen} toggle={toggle}/>
      <Navbar toggle={toggle}/>
      <HomeOuter>
        <HomeInner>
          <HomeLeft>
            <Pen />
          </HomeLeft>
          <HomeRight>
            <Welcome>Welcome!</Welcome>
            <Introduction>I am Sashank from Nepal.</Introduction>
            <Description>And, this is my personal portfolio where I pen down my thoughts, experiences, and findings.</Description>
            <SocialLinks>
              <Github />
              <Facebook />
              <Instagram />

            </SocialLinks>
          </HomeRight>
        </HomeInner>
      </HomeOuter>
    </>
  )
}

export default Home
