import React from 'react'
import styled from 'styled-components'
import './write.css'

const WriteContainer = styled.div`
  padding-top: 50px;
  display: flex;
  flex-direction: column;
`
const WriteForm = styled.form`
  position: relative;
`
const WriteImage = styled.img`
  width: 70vw;
  height: 250px;
  border-radius: 10px;
  object-fit: cover;
`

const WriteFormGroup = styled.div`
  margin: 0 auto;
  display: flex;
  align-items: center;
`

const WriteSubmit = styled.button`
  position: absolute;
  top: 20px;
  right: 50px;
  color: white;
  background-color: teal;
  padding: 10px;
  border: none;
  border-radius: 10px;
  cursor: pointer;
  font-size: 16px;
`

export default function Write() {
  return (
    <WriteContainer>
      <WriteImage src= {require('../../../content/img/blog-cover-2.jpg').default}/>
      <WriteForm>
        <WriteFormGroup>
          <label className="writeIcon" htmlFor="fileInput">
            <i className="fas fa-plus"></i>
          </label>
          <input type="file" id="fileInput" style={{display:"none"}}/>
          <input className="writeInput" type="text" placeholder="Title" autoFocus={true}/>
        </WriteFormGroup>
        <WriteFormGroup>
          <textarea className= "writeText" placeholder="Tell your story..." type="text"></textarea>
        </WriteFormGroup>
        <WriteSubmit>Publish</WriteSubmit>
      </WriteForm> 
    </WriteContainer>
  )
}
