import React from 'react'
import styled from 'styled-components'
import SideBar from '../components/BlogFolder/BlogPage/Sidebar'

const SettingsContainer = styled.div`
  display: flex;
`

const SettingsWrapper = styled.div`
  flex: 9;
  padding: 20px;
`

const SettingsTitle = styled.div`
  display: flex;
  align-items: center;
  justify-content: space-between;
`

const SettingsUpdateTitle = styled.span`
  font-size: 30px;
  margin-bottom: 20px;
  color: lightcoral;

`

const SettingsDeleteTitle = styled.span`
  color: red;
  font-size: 12px;
  cursor: pointer;
`
const SettingsForm = styled.form`
  display: flex;
  flex-direction: column;
`

const SettingsPp = styled.div`
  display: flex;
  align-items: center;
  margin: 10px 0 ;
`

const ProfilePicture = styled.img`
  width: 70px;
  height: 70px;
  border-radius: 20px;
  object-fit: cover;
`

const ProfilePictureIcon = styled.span`
  width: 25px;
  height: 25px;
  border-radius: 50%;
  background-color: lightcoral;
  color: white;
  display: flex;
  align-items: ;

`
const SettingsSubmit = styled.button`
`

export default function Settings() {
  return (
    <SettingsContainer>
      <SettingsWrapper>
        <SettingsTitle>
          <SettingsUpdateTitle>Update Your Account</SettingsUpdateTitle>
          <SettingsDeleteTitle>Delete Account</SettingsDeleteTitle>
        </SettingsTitle>
        <SettingsForm>
          <label htmlFor="">Profile Picture</label>
          <SettingsPp>
            <ProfilePicture src={require('../content/img/knahsas.jpg').default}/>
            <label htmlFor="fileInput"><ProfilePictureIcon><i className = "far fa-user-circle"></i></ProfilePictureIcon></label>
          <input type="file" id="fileInput" style={{display:"none"}}/>
          </SettingsPp>
          <label htmlFor="">Username</label>
          <input type="text" placeholder = "Sashank"/>
          <label htmlFor="">Email</label>
          <input type="email" placeholder = "abc@xyz.com"/>
          <label htmlFor="">Password</label>
          <input type="password"/>
          <SettingsSubmit>Update</SettingsSubmit>
        </SettingsForm>
      </SettingsWrapper>
      <SideBar />
    </SettingsContainer>
  )
}
