import {OuterFooter, FooterContainer, FooterLeft, FooterRight, FooterLinks} from './FooterElements'
import config from '../../website-config'
import {useState, useEffect} from 'react'

const Links = [{
  text: 'Latest Posts',
  link: '/blog',
  displayMobile: true,
},
  {
    text: 'Github',
    link: config.github,
    displayMobile: false,
  }, {
    text: 'Twitter',
    link: config.twitter,
    displayMobile: false,
  }, {
    text: 'Contact',
    link: '/contact',
    displayMobile: true,
  }]

const Footer = ()=> {
  const [dimensions, setDimensions] = useState({
    height: window.innerHeight,
    width: window.innerWidth
  })
  const width = window.innerWidth
  console.log(width)
  return(
    <OuterFooter>
      <FooterContainer>
        <FooterLeft>
          {config.title} &copy; {new Date().getFullYear()}
        </FooterLeft>
        <FooterRight>
          {Links.map((text, index) => {
            if (width > 768 || text.displayMobile){
              return(
                <FooterLinks key={index}>{text.text}</FooterLinks>
              )
            } else {
              return (
                <></>
              )
            }
          } )}
        </FooterRight>
      </FooterContainer>
    </OuterFooter>
  )
}

window.onreize = Footer;



export default Footer;
