import React from 'react'
import styled from 'styled-components'
import { css } from '@emotion/react'

const WriteContainer = styled.div`
  padding-top: 50px;
`
const WriteForm = styled.form`

`

const WriteFormGroup = styled.div`
  margin-left: 150px;
  display: flex;
  align-items: center;
`
const WriteIcon = css`
  width: 25px;
  height: 25px;
  border-radius: 50%;
  border: 1px solid;
  display: flex;
  justify-content: center;
  font-size: 20px;
  color: grey;
  cursor: pointer;
`

const WriteInput = css`
  font-size: 30px;
  border: none;
  padding: 20px;
  width: 70vw;


`

const WriteText = css`

`

const WriteSubmit = css`

`


export default function Write() {
  return (
    <WriteContainer>
      <WriteForm>
        <WriteFormGroup>
          <label htmlFor="fileInput">
            <i className="fas fa-plus"></i>
          </label>
          <input css={WriteIcon} type="file" id="fileInput" style={{display:"none"}}/>
          <input type="text" placeholder="Title" autoFocus={true}/>
        </WriteFormGroup>
        <WriteFormGroup>
          <textarea placeholder="Tell your story..." type="text"></textarea>
        </WriteFormGroup>
        <button>Publish</button>
      </WriteForm> 
    </WriteContainer>
  )
}
