import styled from 'styled-components';

export const OuterContainer = styled.div`
  margin: 0;
  padding: 0;
`

export const BlogHeader = styled.div`
  margin-top: 10;
`

export const HeaderTitles = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  color: #444;
  position: relative;
  background: black;
`
export const BlogSmallTitle = styled.div`
  position: absolute;
  top: 10px;
  font-size: 20px;
  font-family: 'Dancing Script', cursive;
  color: white;
  background: black;
`

export const BlogBigTitle = styled.span`  
  position: absolute;
  top: 25px;
  font-size: 100px;
  font-family: 'Dancing Script', cursive;
  color: white;
  background: black;
`

export const BlogCover = styled.img`
  padding-top: 3px;
  width: 100%;
  height: 450px;
  margin-top: 80px;
  object-fit: cover;
`

export const BlogContainer = styled.div`

`
